import { getStorage, ref, getDownloadURL } from "firebase/storage";

// Create a reference to the storage service
const storage = getStorage();

// Retrieve the image URL from Firebase Firestore or Realtime Database

export default {
	async getCarsImages(imagesData) {
		// let carsUrl = [];
		// const urlList = [];
		try {
		let promisedUrls = 	imagesData.map((pic) => {
				const imageRef = ref(storage, pic);
				return getDownloadURL(imageRef)
					// .then((url) => {
					// 	carsUrl.push(url);
					// })
					// .catch((error) => {
					// 	// Handle any errors that occurred during fetching the image
					// 	console.error("Error fetching image:", error);
					// });

				// carsImages.push({ carId: id, carImagesUrl: urlList });
		});
			console.log(promisedUrls,'promises')
		let carsUrl = 	await Promise.all(promisedUrls)
			console.log(carsUrl, "la url")
			return carsUrl;
		} catch (error) {
			throw error;
		}
	},
};
