export default {
	methods: {
		toggleLinksVisibility(link) {
			let showNav = true;
			if (
				(link == "Dashboard" && !this.isLoggedIn) ||
				(link == "Join Us" && this.isLoggedIn) ||
				(link == "Log out" && !this.isLoggedIn)
			) {
				showNav = false;
			}
			return showNav;
		},
	},
};
