import {
	getAuth,
	createUserWithEmailAndPassword,
	signInWithEmailAndPassword,
	signOut,
} from "firebase/auth";
import profile from "./profile";

//global timer for expiration token
let timer;
export default {
	namespaced: true,
	state: {
		user: null,
		alert: {},
	},
	mutations: {
		SET_USER(state, payload) {
			state.user = payload;
			console.log(payload);
		},
		SET_ALERT_MSG(state, payload) {
			state.alert = payload;
			console.log(state.alert, "el test");
		},
		SHOULD_SHOW_NAV(link) {
			let showNav = true;
			if (
				(link == "DASHBOARD" && !this.isLoggedIn) ||
				(link == "JOIN US" && this.isLoggedIn) ||
				(link == "SIGN OUT" && !this.isLoggedIn)
			) {
				showNav = false;
			}
			return showNav;
		},
	},
	actions: {
		async signUp({ commit }, { email, password }) {
			try {
				let response = await createUserWithEmailAndPassword(
					getAuth(),
					email,
					password
				);
				return response.user;
			} catch (error) {
				throw error;
			}
		},
		async signIn({ commit, dispatch }, { email, password }) {
			try {
				clearTimeout(timer);
				let response = await signInWithEmailAndPassword(
					getAuth(),
					email,
					password
				);
				const currentUser = response._tokenResponse.email;
				const token = response._tokenResponse.idToken;
				const expiresIn = response._tokenResponse.expiresIn;
				const userProfile = await profile.fetchByAuthId(response.uid);
				const isActive = !!userProfile.active;

				timer = setTimeout(() => {
					dispatch("singOutUser");
				}, expiresIn);

				commit("SET_USER", { currentUser, token, isActive });
				localStorage.setItem(
					"currentUserDealer",
					JSON.stringify({ currentUser, token, isActive })
				);

				return response.user;
			} catch (error) {
				throw error;
			}
		},
		async singOutUser(_) {
			try {
				await signOut(getAuth());
				localStorage.removeItem("currentUserDealer");
				this.user = null;
				clearTimeout(timer);
			} catch (error) {}
		},
	},
	getters: {
		isLoggedIn: (state) => !!(state.user && state.user.isActive),
	},
};
