import {
	getAuth,
	createUserWithEmailAndPassword,
	signInWithEmailAndPassword,
	signOut
} from "firebase/auth";

//global timer for expiration token
let timer;
export default {
	namespaced: true,
	state: {
		user: null,
		alert: {},
	},
	mutations: {
		SET_USER(state, payload) {
			state.user = payload;
			console.log(payload);
		},
		SET_ALERT_MSG(state, payload) {
			state.alert = payload;
			console.log(state.alert, "el test");
		},
		SHOULD_SHOW_NAV(link) {
			let showNav = true;
			if (
				(link == "DASHBOARD" && !this.isLoggedIn) ||
				(link == "JOIN US" && this.isLoggedIn) ||
				(link == "SIGN OUT" && !this.isLoggedIn)
			) {
				showNav = false;
			}
			return showNav;
		},
	},
	actions: {
		async signUp({ commit }, { email, password }) {
			try {
				let response = await createUserWithEmailAndPassword(
					getAuth(),
					email,
					password
				);
				return response.user;
			} catch (error) {
				throw error;
			}
		},
		async signIn({ commit }, { email, password }) {
			try {
				let response = await signInWithEmailAndPassword(
					getAuth(),
					email,
					password
				);
				const currentUser = response._tokenResponse.email;
				const token = response._tokenResponse.idToken;
				const expiresIn = response._tokenResponse.expiresIn;

				timer = setTimeout(() => {
					dispatch("signOut");
				}, expiresIn);
				
				commit("SET_USER", { currentUser, token });
				localStorage.setItem(
					"currentUserDealer",
					JSON.stringify({ currentUser, token })
				);

				return response.user;
			} catch (error) {
				throw error;
			}
		},
		async
	},
	getters: {},
};
